mapper.insert:
insert into news (id , title , href , abstract1 , abstract2 , pureText , coverPic ,
newsPic , content , getDate , source , isTop, clicks , newsType
) values (
#{id} , #{title} , #{href} , #{abstract1} , #{abstract2} , #{pureText} , #{coverPic} ,
#{newsPic} , #{content} , #{getDate} , #{source} , #{isTop} , #{clicks} , #{newsType}
)
